import { StatusBar } from 'expo-status-bar';
import React from 'react';
import {  ImageBackground, StyleSheet, Text, View, Image, TextInput, Button, Alert } from 'react-native';

export default function App() {
  return (
    <ImageBackground source={require('./assets/backgroundLogin.jpg')} style={styles.background}>
    <View style={styles.body}>

      {/* <Text>Enfim meu primeiro APP!</Text>
      <StatusBar style="auto" /> */}
      <View style={styles.navbar}>
      <Image source={require('./assets/logo2.png')} style={styles.logo}/>
      </View>
      <View style={styles.container}>


        <TextInput style={styles.input} placeholder="Digite seu usuario"/>

        <TextInput style={styles.input} placeholder="Digite sua senha"
        secureTextEntry={true} />

        <Button style={styles.login}  title="  Login  " onPress={() => Alert.alert('Alerta de clique!')}/> 
        <Button style={styles.login} title="limpa" onPress={() => Alert.alert('Alerta de clique!')} />
      </View>
      <View style={styles.bottombar}>
        <Text style={styles.item}>Home</Text>
        <Text style={styles.item}>Produtos</Text>
        <Text style={styles.item}>Contato</Text>
      </View>
    </View>
    </ImageBackground>
  );
}

const styles = StyleSheet.create({
  body:{
    flex: 1,
    backgroundColor: 'rgba(255, 247, 0, 0.800)',
  },
  background:{
    width: '100%',
    height: 560,
    justifyContent: 'center',
  },
  logo:{
    marginTop: '150px',
    width: '250px',
    height: '100px'
  },

  navbar:{

    alignItems: 'center',
    justifyContent: 'center',
  },
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },

  input:{
    marginTop: 10,
    padding: 10,
    width: 250,

    textAlign: 'center',

    color: '#fff',
    borderBottomWidth: '2px',
    borderBottomColor: '#fff',

    fontSize: 15,

  },
  bottombar:{
    flexDirection: 'row',
    justifyContent: 'center',
    backgroundColor: 'rgba(0, 171, 250, 1.0)'

  },
  item:{
    padding: 7,
    fontSize: 20,

    color: '#fff',
    fontFamily: 'monospace'

  },
  login: {
    width: 150,

  }
});
