import { Card, Tile } from 'react-native-elements'
import React from 'react';
import { View, Text, FlatList, StyleSheet } from 'react-native';
import Menu from '../components/Menu'

const Lojas = (props) => {
    const data = [
        {
            endereco: 'Avenida do Estado, 5533',
            complemento: 'Sala 32',
            cidade: 'São Paulo',
            foto: 'saopaulo.jpg'
        },
        {
            endereco: 'Centro Empresarial Mourisco | Praia de Botafogo, 501',
            complemento: '5º e 6º Andar',
            cidade: 'Rio de Janeiro',
            foto: 'riodejaneiro.jpg'
        },
        {
            endereco: 'Avenida Conde de Boa Vista, 800',
            complemento: '3º, 8º e 9º Andar',
            cidade: 'Recife',
            foto: 'recife.jpg'
        }
    ]

      return (
  <View>
    <Menu navigation={props.navigation}/>

  {
    data.map((d, i) => {
      return (
        <Card key={i}>
        <Tile
        imageSrc={require(`../../assets/${d.foto}`)}
        title={d.cidade}
        titleStyle={{font}}
        featured
      />
          <Text style={styles.cidade}>{d.cidade}</Text>
          <img style={{width: 310, height: 200}} src={require(`../../assets/${d.foto}`)} />
          <Text>{d.endereco}</Text>
          <Text>{d.complemento}</Text>
        </Card>

      );
    })
}
  </View>
)};

const styles = StyleSheet.create({ 
    imagem:{
        width: 310,
        height: 200
    },
    nome:{
        textAlign: 'center',
        fontSize: 50,
        fontFamily: 'sans-serif',

    }
})

export default Lojas;
